#include<stdio.h>
int maxyueshu(int m,int n)  

{  int i=1,t;  
for(;i<=m&&i<=n;i++)  
{if(m%i==0&&n%i==0)  
 t=i;  
}  
return(t);  
}  
int minbeishu(int m,int n)  

{int j;  
if(m>=n) j=m;  
else j=n;  
for(;!(j%m==0&&j%n==0);j++);  
return j;  
}  
int main()  
{int a,b,max,min;  
//printf("enter two number is: ");  
scanf("%d%d",&a,&b);  
max=maxyueshu(a,b);  
min=minbeishu(a,b);  
printf("%d %d",max,min);  
return 0;
}

#include<stdio.h>
#include"math.h"  
float yishigen(m,n,k)  
float m,n,k;  
{float x1,x2;  
x1=(-n+sqrt(k))/(2*m);  
x2=(-n-sqrt(k))/(2*m);  
printf("x1=%.3f x2=%.3f\n",x1,x2);  
}  
float denggen(m,n)  
float m,n;  
{float x;  
x=-n/(2*m);  
printf("x1=%.3f x2=%.3f\n",x,x);  
}  
float xugen(m,n,k)  
float m,n,k;  
{float x,y;  
x=-n/(2*m);  
y=sqrt(-k)/(2*m);  
printf("x1=%.3f+%.3fi x2=%.3f-%.3fi\n",x,y,x,y);  
}  
main()  
{float a,b,c,q;  
//printf("input a b c is ");  
scanf("%f%f%f",&a,&b,&c);  
//printf("\n");  
q=b*b-4*a*c;  
if(q>0) yishigen(a,b,q);  
else if(q==0) denggen(a,b);  
else xugen(a,b,q);  
}

psushu(m)  
int m;  
{
	int i=2,t;  
	for(;i<=m;i++)  
		if(m%i==0&&i<m) break;  
	if(i==m+1) t=1;  
		else t=0;  
	return t;  
}  
main()  
{int a,s;  
//printf("enter sushu is \n");  
scanf("%d",&a);  
s=psushu(a);  
if(s==1) printf("prime");  
else printf("not prime");  
}

