fanxu(str1)  
char str1[100];  
{int i,t,j;  
char str2[100];strcpy(str2,str1);  
t=strlen(str1);  
for(i=0,j=t-1;j>-1;i++,j--)  
str1[i]=str2[j];
str1[i--]=0;
}  

