#include"math.h"  
#define SSS(m,n,k) (m+n+k)/2  
#define AQRT(m,n,k) sqrt(SSS(m,n,k)*(SSS(m,n,k)-m)*(SSS(m,n,k)-n)*(SSS(m,n,k)-k))  
main()  
{float a,b,c,s,area;  
scanf("%f%f%f",&a,&b,&c);  
s=SSS(a,b,c);  
area=AQRT(a,b,c);  
printf("%.3f\n",area);  
}

