#include<stdio.h>
#include<string.h>

void Swap(char **q1, char**q2);

int main()
{    
    int i=0;
    char *p1,*p2,*p3, str1[80],str2[80],str3[80];
 p1=str1; p2=str2; p3=str3;
    gets(str1);
	gets(str2);
	gets(str3);
if (strcmp(p1, p2)>0)
        Swap(&p1, &p2);  
if (strcmp(p1, p3)>0)
        Swap(&p1, &p3);
if (strcmp(p2, p3)>0)
        Swap(&p2, &p3);
 printf("%s\n%s\n%s\n",p1,p2,p3);
 return(0);
}

void Swap(char **q1, char* *q2)
{char *t;
 t= *q1;
 *q1 = *q2;
 *q2 = t;
}

#include<stdio.h>
int main()
{
	int array[11];
	int n=10;
	//scanf("%d",&n);
	int temp;
	int i;
	for(i=0;i<n;i++)
		scanf("%d",&array[i]);
	temp=0;
	for(i=0;i<n;i++)
		if(array[temp] > array[i])
			temp = i;
		int t = array[temp];
		array[temp] = array[0];
		array[0] = t;
	temp = 0;
	for(i=0;i<n;i++)
		if(array[temp] < array[i])
			temp = i;
		t = array[temp];
		array[temp] = array[n-1];
		array[n-1] = t;
	for(i=0;i<n;i++)
	{

		printf("%d ",array[i]);
	}
	printf("\n");
	return 0;
}

#include <stdio.h>
void mv(int *p,int n,int m)
{
    int *h, t,i;
    h=p+n-m-1; 
    for(;h>=p;h--)
    {
         for(i=0;i<m;i++) 
         {
                     t=*(h+i);
                     *(h+i)=*(h+i+1);
                     *(h+i+1)=t;
          }
   }
}


int main()
{
    int a[80];
     int m,i,n;
	 scanf("%d",&n);
 for(i=0;i<n;i++) 
	 scanf("%d",&a[i]);
   scanf("%d",&m);
  mv(a,n,m); 
 for(i=0;i<n;i++) 
	 printf("%d ",a[i]);

  return 0;
}

