#include<stdio.h>
#define N 100 
struct student
{
char num[6];
char name[8];
int score[4];
int avr;
}stu[N];
int r;

int main()
{
  int sum3=0,sum1=0,sum2=0,maxi;
  int sum;
  int max;
  scanf("%d",&r);
  int i,j;
  for(i=0;i<r;i++)
  {
   scanf("%s%s",stu[i].num,stu[i].name);
   for(j=0;j<3;j++)
   {
    scanf("%d",&stu[i].score[j]);
   }

}

max=0;
maxi=0;
 for(i=0;i<r;i++)
 {
  sum=0;
  sum1+=stu[i].score[0];
  sum2+=stu[i].score[1];
  sum3+=stu[i].score[2];
  for(j=0;j<3;j++)
    sum+=stu[i].score[j];
  stu[i].avr=sum/3;

  if(sum>max)
  {
  max=sum;
  maxi=i;
  }

 }
printf("%d %d %d",sum1/r,sum2/r,sum3/r);
putchar('\n');

printf("%s %s %d %d %d",stu[maxi].num,stu[maxi].name,stu[maxi].score[0],stu[maxi].score[1],stu[maxi].score[2]);


return 0;
}

import java.util.Scanner;
import java.io.*;
public class Main {
	public static Scanner cin=new Scanner(System.in);
	public static void main(String[] args){
		int n=cin.nextInt();
		int m=cin.nextInt();
		int num[][]=new int[m+n][2];
		int tmp[]={0,0};
		for(int i=0;i<m+n;i++)
		{
			for(int j=0;j<2;j++)
			{
				num[i][j]=cin.nextInt();
			}
		}
		for(int i=0;i<m+n-1;i++)
		{
			for(int j=i+1;j<m+n;j++)
			{
				if(num[j][0]<num[i][0])
				{
					tmp=num[i];
					num[i]=num[j];
					num[j]=tmp;
				}
			}
		}
		for(int i=0;i<m+n;i++)
		{
			System.out.println(num[i][0]+" "+num[i][1]);
		}


	}

}
