/* There's a 5-case analytic solution but why bother? */

#include <stdio.h>

main(){
   int i,j,k,m,n,s,d;

   while (2 == scanf("%d%d",&s,&d)){
      int best = -9999999, tot;
      for (i=0;i<4096;i++){
         for (j=0;j<8;j++){
            for (tot=k=0;k<5;k++) tot += ((i>>(j+k))&1)?s:-d;
            if (tot >= 0) break;
         }
         if (j<8) continue;
         for (tot=j=0;j<12;j++) tot += ((i>>j)&1)?s:-d;
         if (tot > best) best = tot;
      }
      if (best > 0) printf("%d\n",best);
      else printf("Deficit\n");
   }
}

#include <stdio.h>
#include <math.h>

double lat[1000], lng[1000], x[1000], y[1000], z[1000];
double d, bestcos = -1, mincos;
int n, besti;

main(){
   int i,j,k;
   scanf("%d",&n);
   for (i=0;i<n;i++) {
      scanf("%lf%lf",&lat[i],&lng[i]);
      z[i] = sin(lat[i]*M_PI/180);
      x[i] = cos(lng[i]*M_PI/180) * cos(lat[i]*M_PI/180);
      y[i] = sin(lng[i]*M_PI/180) * cos(lat[i]*M_PI/180);
   }
   for (i=0;i<n;i++){
      mincos = 1;
      for (j=0;j<n;j++){
         d = x[i]*x[j] + y[i]*y[j] + z[i]*z[j];
         if (d < mincos) mincos = d;
      }
      if (mincos > bestcos) {
         bestcos = mincos;
         besti = i;
      }
   }
   printf("%0.2lf %0.2lf\n",lat[besti],lng[besti]);
}

#include <stdio.h>
#include <math.h>

int n;
double x[1000], y[1000], r[1000];
int top[1000];

int possible = 1;
double ne = 1000, nw = 1000;

main(){
   int i,j,k;
   scanf("%d",&n);
   for (i=0;i<n;i++) scanf("%lf%lf%lf",&x[i],&y[i],&r[i]);
   for (i=0;i<n;i++) if (y[i]+r[i] > 1000) visit(i);
   if (possible) printf(
     "Bill enters at (0.00, %0.2lf) and leaves at (1000.00, %0.2lf).\n",nw,ne);
   else printf("Bill will be bitten.\n");
}

visit(int i){
   int j,k;
   double yy;
   if (top[i]++) return;

   for (j=0;j<n;j++) {
      if (hypot(x[j]-x[i],y[j]-y[i]) < r[i]+r[j]) visit(j);
   }
   if (y[i]-r[i] < 0) possible = 0;
   if (x[i]-r[i] < 0) {
      yy = y[i] - sqrt(r[i]*r[i] - x[i]*x[i]);
      if (yy < nw) nw = yy;
   }
   if (x[i]+r[i] > 1000) {
      yy = y[i] - sqrt(r[i]*r[i] - (1000-x[i])*(1000-x[i]));
      if (yy < ne) ne = yy;
   }
}

/* use of random() portable among:

   Solaris, SunOS, DECUnix, Linux

*/

#include <stdio.h>
#include <stdlib.h>

char jane[200], john[200];
int janei, johni;

main(){
   int i,j;
   gets(jane);
   gets(john);
   for (j=janei=johni=0;j<1000 && strlen(jane)&&strlen(john);j++){
      if (jane[janei] == john[johni]) {
         if (random()/141%2 == 0) { /* jane wins */
            printf("Snap! for Jane: ");
            for (i=strlen(jane);i>janei;i--) jane[i+johni+1] = jane[i];
            strncpy(jane+janei+1,john,johni+1);
            strcpy(john,john+johni+1);
            janei = janei + johni + 2;
            johni = 0;
            for (i=janei-1;i>=0;i--) printf("%c",jane[i]);
            printf("\n");
         }else{  /* john wins */ 
            printf("Snap! for John: ");
            for (i=strlen(john);i>johni;i--) john[i+janei+1] = john[i];
            strncpy(john+johni+1,jane,janei+1);
            strcpy(jane,jane+janei+1);
            johni = johni + janei + 2;
            janei = 0;
            for (i=johni-1;i>=0;i--) printf("%c",john[i]);
            printf("\n");
         }
      }else{
         janei++; johni++;
      }
      if (!jane[janei]) janei = 0;
      if (!john[johni]) johni = 0;
   }
   if (!strlen(jane)) printf("John wins.\n");
   else if (!strlen(john)) printf("Jane wins.\n");
   else printf("No winner after %d turns\n",j);
}

/* Don't need the array; doesn't need to be so big; who cares? */

#include <stdio.h>

long long most[1000000];

main(){
   long long i;
   int j,n,x,y;
   for (i=0;i<500000;i++){
      most[2*i] = i*(i+1);
      most[2*i+1] = (i+1)*(i+1);
   }
   scanf("%d",&n);
   while (n--) {
      scanf("%d%d",&x,&y);
      for (j=0;most[j] < y-x;j++);
      printf("%d\n",j);
   }
}
