#define ONE_ANSWER 1

int n, d[500][500];
int nf, f[100];
int fd[500];

main() {
	int i, j, k, x, y, z, min, nmin;

	scanf( " %d %d", &nf, &n );
	for( i = 0; i < nf; i++ ) {
		scanf( " %d", &f[i] );
		f[i]--;
	}
	for( i = 0; i < n; i++ ) for( j = 0; j < n; j++ ) d[i][j] = 1000000000;
	for( i = 0; i < n; i++ ) d[i][i] = 0;
	while( scanf( " %d %d %d", &x, &y, &z ) == 3 ) {
		x--; y--;
		d[x][y] = d[y][x] = z;
	}
	for( i = 0; i < n; i++ ) for( j = 0; j < n; j++ ) for( k = 0; k < n; k++ ) {
		x = d[j][i] + d[i][k];
		if( d[j][k] > x ) d[j][k] = x;
	}
	for( i = 0; i < n; i++ ) {
		fd[i] = 1000000000;
		for( j = 0; j < nf; j++ )
			if( d[i][f[j]] < fd[i] ) fd[i] = d[i][f[j]];
	}
	nmin = -1; min = 1000000000;
	for( i = 0; i < n; i++ ) {
		x = -1;
		for( j = 0; j < n; j++ ) {
			y = (d[j][i]<fd[j]) ? d[j][i] : fd[j];
			if( y > x ) x = y;
		}
		if( x < min ) {
			nmin = i; 
			min = x;
		}
	}
#if !ONE_ANSWER
	printf( "Possible answers:" );
	for( i = 0; i < n; i++ ) {
		x = -1;
		for( j = 0; j < n; j++ ) {
			y = (d[j][i]<fd[j]) ? d[j][i] : fd[j];
			if( y > x ) x = y;
		}
		if( x == min ) printf( " %d", i+1 );
	}
	putchar( '\n' );
#else
	printf( "%d\n", nmin+1 );
#endif
}

#include <stdio.h>

char alph[27] = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
char code[27] = "01230120022455012623010202";

main() {
	int ch;
	char last = '0';

	while( (ch=getchar()) != EOF ) {
		if( ch == '\n' ) {
			putchar( '\n' );
			last = '0';
		} else if( code[ch-'A'] != last ) {
			last = code[ch-'A'];
			if( last != '0' ) putchar( last );
		}
	}
}

#include <stdio.h>
#include <stdlib.h>

int ar[201][10001];
int len;

void pr( int i, int j ) {
	if( i == 0 ) return;
	pr( i-1, ar[i][j] );
	printf( (j == ar[i][j]) ? "port\n" : "starboard\n" );
}

main() {
	int i, j, k, x, y, z, n;

	scanf( " %d", &len );
	len *= 100;
	for( i = 0; i <= 200; i++ ) for( j = 0; j <= 10000; j++ ) ar[i][j] = -1;
	ar[0][0] = 0;
	y = 0;
	for( i = 0; i < 200; i++ ) {
		scanf( " %d", &x );
		if( !x ) break;
		y += x;
		z = 0;
		for( j = 0; j <= 10000; j++ ) if( ar[i][j] >= 0 ) {
			if( j+x <= len && y-x-j <= len ) {ar[i+1][j+x] = j; z = 1;}
			if( j <= len && y-j <= len ) {ar[i+1][j] = j; z = 1;}
		}
		if( !z ) break;
	}
	printf( "%d\n", i );
	for( j = 0; j <= 10000; j++ ) if( ar[i][j] >= 0 ) break;
	pr( i, j );
}

#include <math.h>

main() {
	double dx, dy, gx, gy;
	double x, y;
	double dd, gd;

	scanf( " %lf %lf %lf %lf", &gx, &gy, &dx, &dy );
	while( scanf( " %lf %lf", &x, &y ) == 2 ) {
		dd = hypot( dx-x, dy-y );
		gd = hypot( gx-x, gy-y );
		if( gd * 2.0 < dd ) {
			printf( "The gopher can escape through the hole at "
					"(%.3f,%.3f).\n", x, y );
			return;
		}
	}
	printf( "The gopher cannot escape.\n" );
}

char dig[50];

int ccmp( void *a, void *b ) {
	return *((char*)a)-(int)*((char*)b);
}

main() {
	int i, j, k, x, y, z, n;

	for(;;) {
		scanf( " %s", dig );
		if( !dig[1] ) break;
		n = strlen(dig);
		dig[n-2] = dig[n-1];
		dig[n-1] = 0;
		n--;
		for( i = n-1; i >= 0; i-- ) {
			for( j = dig[i]+1; j <= '9'; j++ ) {
				for( k = i; dig[k]; k++ ) {
					if( dig[k] == j ||
						dig[k] == '2' && j == '5' ||
						dig[k] == '5' && j == '2' ||
						dig[k] == '6' && j == '9' ||
						dig[k] == '9' && j == '6' ) {
						dig[k] = dig[i];
						dig[i] = j;
						goto found;
					}
				}
			}
		}
found:
		if( i < 0 ) {
			printf( "The price cannot be raised.\n" );
			continue;
		}
		for( k = i+1; dig[k]; k++ ) {
			if( dig[k] == '5' ) dig[k] = '2';
			if( dig[k] == '9' ) dig[k] = '6';
		}
		qsort( &dig[i+1], n-i-1, sizeof(char), ccmp );
		dig[n] = dig[n-1];
		dig[n-1] = '.';
		printf( "%s\n", dig );
	}
}
